function rot13(str) { // LBH QVQ VG!
var ans = ''
  for(let i = 0; i < str.length; i++){
      //check if alpha
      if (str.charCodeAt(i) <65 || str.charCodeAt(i) > 90){
        ans += str[i]
      }
      else if (str.charCodeAt(i) >=65 && str.charCodeAt(i) <=77){
          ans += String.fromCharCode(str.charCodeAt(i) + 13)
      }
      else {
          ans += String.fromCharCode(str.charCodeAt(i) - 13)
        }
  }
  console.log(ans)
  return ans;
}

